        delete _tokenApprovals[tokenId];

        // 関係するマッピングを適切に変更している
        _balances[owner] -= 1;
        delete _owners[tokenId];

        // フロントエンド等に向けたeventをemitしている
        emit Transfer(owner, address(0), tokenId);

        // トークンの移動後に実行されるメソッドを呼び出す
        _afterTokenTransfer(owner, address(0), tokenId);
